INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (1116, 'Matemática','*', '', '||A:43%#@||B:57,5%#@||C:61,5%#@||D:73%#@||E:77%#@', '', '', 'Leia o texto a seguir. “A cidade de São Paulo atingiu 23% da meta de vacinação contra sarampo entre os jovens de 15 a 29 anos. Segundo a Secretaria Municipal de Saúde, de 10 de junho até esta quintafeira (25), foram aplicadas 720 mil doses da vacina.” Disponível em https://aconteceagora.com.br/spatingiu-23-dametadevacinacaocontrasarampoentrejovensde-15-a-29-anos/?utm_source=rss&utm_medium=rss&utm_campaign=spatingiu-23-dametadevacinacaocontrasarampoentrejovensde-15-a-29-anos. Acesso em 26/07/2019.  Na cidade de São Paulo, após 25 de julho, foram vacinados 50% dos jovens de 15 a 19 anos que ainda não haviam recebido a vacina. Qual é a porcentagem total de jovens vacinados até essa data?', '19', 30);
INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (1117, 'Matemática','E', '', '||A:16#@||B:18#@||C:20#@||D:22#@||E:24#@', '', '', 'Uma  loja  de  eletrodomésticos  possui  uma  matriz  e  onze  filiais.  Ela  comprou  200  televisores  idênticos para serem  distribuídos  igualmente  entre  as  12  lojas,  ficando  a  matriz  também  com  o  resto  da  divisão.  O  número  de televisores destinados à matriz equivale a:', '20', 30);
INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (1118, 'Matemática','C', '', '||A:2+√3#@||B:12−√3#@||C:2−√3#@||D:√3−1#@||E:#@', '', '', 'Na  figura  a  seguir,  temos  três  circunferências  de  raio 1,  tangentes  entre  si  e  inscritas  no  retângulo 퐴퐵퐶퐷. Sabendo que 푀 é ponto do segmento 퐴퐷̅̅̅̅ e que 퐹, 퐺 e 퐸 são pontos de tangência entre as circunferências e os lados do retângulo, calcule o valor da tangente do ângulo 푀퐸̂퐹.', '21', 30);
