INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (1165, 'Ciências Naturais','E', '', '||A:Sim, a reprodução sexuada subentende a presença de relações sexuais com fecundação interna.#@||B:Sim, pois, embora os pássaros fêmea e macho produzam gametas, o macho não copula a fêmea.#@||C:Não, pois a reprodução assexuada só ocorre em microorganismos.#@||D:Não, pois a presença de gametas, por si só, já torna a reprodução sexuada.#@||E:Não,  pois, embora  os  pássaros  machos  não  possuam  órgão  copulador,  ocorre  compartilhamento  de#@', '', '', 'Um aluno, ao estudar a reprodução do canáriodoreino, um pássaro canoro, descobriu que os machos desse grupo  animal  não  possuem  órgão  copulador,  como  pênis.  O  aluno  deduziu,  com  isso,  que  os  pássaros  fazem reprodução assexuada. Com base em seus conhecimentos, você poderia dizer que o aluno está correto?', '33', 31);
