INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (1167, 'Ciências Naturais','E', '', '||A:Isotopia.#@||B:Isobaria.#@||C:Isotonia.#@||D:Isomeria.#@||E:Alotropia.#@', '', '', 'As diferentes formas de organização dos átomos de um elemento químico permitem configurações bastante distintas de propriedades  físicas  e  químicas. De  acordo  com  o  texto,  o estanho  pode  apresentar  três  formas. Assinale a alternativa que descreve este fenômeno.', '35', 31);
INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (1168, 'Português','E', '', '||A:o homem ao centro já conhecia os soldados antes de chegarem à ilha.#@||B:a palavra “tipo” tem sentidos diferentes para o soldado e para o homem ajoelhado.#@||C:o armamento usado pelos soldados é necessário para a defesa deles.#@||D:o homem vestido só de bermuda está muito doente, por isso foi exilado de seu país.#@||E:a palavra “civilização” tem sentidos diferentes para o soldado e para o homem ajoelhado.#@', '', '', 'A interpretação da imagem e dos diálogos, em conjunto, sugere que:', '1', 32);
