INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (1213, 'Português','C', '', '||A:tristeza.#@||B:abominação.#@||C:arrogância.#@||D:ócio.#@||E:prazer.#@', '', '', 'Muitas  vezes,  durante  a  leitura  dos  mais  variados  gêneros  textuais,  encontramos  palavras  desconhecidas  e seu   significado   deve   ser   compreendido   pelo   contexto   em   que   elas   estão   inseridas.   Sendo   assim,   uma interpretação possível para “fel” (verso 15), nessa música, seria:', '11', 33);
INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (1214, 'Português','D', '', '||A:“tão vazias”.#@||B:“os bares”.#@||C:“estão cheios”.#@||D:“de almas tão vazias”.#@||E:“cheios de almas”.#@', '', '', 'No  trecho  “Os  bares  estão  cheios  de  almas  tão  vazias”,  temos  a  presença  da  figura  de  linguagem denominada antítese. Ela  só é possível, no trecho reproduzido, pois há duas palavras que têm sentidos opostos, sendo uma sintaticamente complementar à outra. Assinale a alternativa que apresenta o termo complementar em que uma dessas palavras é encontrada.', '12', 33);
