INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (1251, 'Matemática','E', '', '||A:1500 candidatos.#@||B:2100 candidatos.#@||C:2700 candidatos.#@||D:3900 candidatos.#@||E:4500 candidatos.#@', '', '', 'Dos 6000 candidatos  que  realizaram  os  exercícios  propostos  em  um  concurso  público, 2500 acertaram  o primeiro e 3000 acertaram o segundo. Sabendo que o número de candidatos que acertou ambos os exercícios é igual à metade daqueles que erraram os dois, quantos acertaram apenas um dos dois exercícios?', '14', 34);
