INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (1253, 'Matemática','E', '', '||A:72 km#@||B:80 km#@||C:84 km#@||D:90 km#@||E:96 km#@', '', '', 'João fez uma viagem de carro entre as cidades A e B. Na ida, usou apenas álcool no tanque e seu carro fez 6 km  por  litro.  Na  volta,  apenas  com  gasolina  no  tanque,  fez 8 km  por  litro.  No  total,  João  gastou 28 litros  de combustível nessa viagem. Qual é a distância entre as cidades A e B?', '16', 34);
INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (1254, 'Matemática','C', '', '||A:#@||B:#@||C:#@||D:#@||E:#@', '', '', 'O  perímetro  de  uma  quadra retangular de  vôlei  é 54 m,  sendo  que  a  rede  a  divide  em  dois  quadrados  de mesma medida. Com essas informações, a medida, em metros, de uma diagonal da quadra é:', '17', 34);
