INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (1256, 'Matemática','B', '', '||A:17#@||B:19#@||C:22#@||D:25#@||E:28#@', '', '', 'A  idade  média  das 7 jogadoras  de  handebol  de  uma  seleção  era  de 27 anos. Ao  substituir  sua  goleira  por outra jogadora de 33 anos, a idade média passou a ser de 29 anos. A idade, em anos, da goleira substituída é:', '19', 34);
