INSERT INTO question(id, topic, answer, images, options, subtitle, initial_text, text, number, test_id) values (1259, 'Matemática','E', '', '||A:10#@||B:8#@||C:6#@||D:4#@||E:2#@', '', '', 'Em uma prova de 30 questões para assinalar Verdadeiro ou Falso, o candidato ganha 4 pontos por questão correta e perde 1 ponto por questão errada. O candidato também pode optar por não responder à questão. Nesse caso, ele não ganha nem perde pontos. Se nessa prova um candidato fez 82 pontos e o número de questões que ele não respondeu foi igual a um terço das questões que ele acertou, quantas questões o candidato errou?', '22', 34);
